#pragma once

#include <cinttypes>
#include <string>

namespace esphome {
namespace display {

enum effects {
    None,
    Time,
    TimeDate,
    Message,
    LoadPNG,
    LoadGIF,
    FillColor,
    FillRainbow,
    RandomPixels,
    Alarm
};

class DeviceState {
 public:
  enum effects mEffect{None};
  uint32_t mEffectIndex{0};

  // switches
  bool    mPowerState{true};
  bool    mPlayState{false};

  // sensors
  uint8_t mConnectionState{0};
  uint8_t mDisplayWidth{32};
  uint8_t mDisplayHeight{32};
  uint8_t mFontFlag{0};
  uint8_t mFontWidth{8};
  uint8_t mFontHeight{16};
  uint8_t mOrientation{0};
  uint8_t mFunMode{0};

  // numbers
  uint8_t mClockStyle{7};
  uint8_t mAnimationSpeed{100};
  uint8_t mSlotNumber{1};

  // color picker
  uint8_t mR{0xFF};
  uint8_t mG{0xFF};
  uint8_t mB{0xFF};
  uint8_t mBrightness{100};
  
  // exclusive numbers (optipnal)
  uint8_t mXpos{0}; // setPixel 
  uint8_t mYpos{0}; // setPixel 

  bool    mShowDate{false};
  uint8_t mAnimationMode{1}; // sendText
  uint8_t mTextMode{0};      // sendText

  // frame buffer for RGB display graphics
  std::vector<uint8_t> framebuffer_;

  // text input
  std::string txt_ = "Zwölf Boxkämpfer jagen Viktor quer über den großen Sylter Deich 1234567890";
  std::string png_ = "";
  std::string gif_{}; // not supported jet
};


}  // namespace display
}  // namespace esphome
